import React from 'react';
import ReactDOM from 'react-dom';

import { LocationData } from '../../../../__mocks__/RefrigeratorLocationMapView.mock';
import RefrigeratorLocationMapView from '../../../../src/components/Asset/RefrigeratorLocationMapView/RefrigeratorLocationMapView.container';

describe('RefrigeratorLocationMapView Component Test Specifications', () => {


  it('should check proptype of data', () => {
    const locationData = {
      node :{
        state: {
          location: {
            latitude: LocationData.latitude,
            longitude: LocationData.longitude
          }
        }

      }
    };
    const cardWrapper = <RefrigeratorLocationMapView data={locationData} />;
    expect( typeof cardWrapper.props.data ).toEqual( 'object' );
  });


  it('should render successfully when params are passed', () => {
    const div = document.createElement('div');
    const locationData = {
      node :{
        state: {
          location: {
            latitude: LocationData.latitude,
            longitude: LocationData.longitude
          }
        }

      }
    };
    ReactDOM.render(<RefrigeratorLocationMapView data={locationData} />, div);

  });

});

