import React from 'react';
import ReactDOM from 'react-dom';
import { shallow } from 'enzyme';

import { DeviceData } from '../../../../__mocks__/EventDetail.mock';
import DeviceContainer from '../../../../src/components/Event/EventDetailPage/DeviceContainer/Device.container';

describe('Device Container Test Specifications', () => {

  it('DataList element count should be equal to 1', () => {
    const deviceData = {
      node :{
        device: DeviceData.name
      }
    };
    const cardWrapper = shallow(<DeviceContainer
      data={deviceData}
    />);
    expect(cardWrapper.find('DataList').length).toEqual(1);
  });

  it('DataListItem element count should be equal to 1', () => {
    const deviceData = {
      node :{
        device: DeviceData.name
      }
    };
    const cardWrapper = shallow(<DeviceContainer
      data={deviceData}
    />);
    expect(cardWrapper.find('DataListItem').length).toEqual(1);
  });

  it('should render successfully when device name is passed', () => {
    const div = document.createElement('div');
    const deviceData = {
      node :{
        sourceDevice: DeviceData.name
      }
    };
    ReactDOM.render(<DeviceContainer
      data={deviceData}
    />, div);
  });

});
