import React from 'react';
import ReactDOM from 'react-dom';
import { shallow } from 'enzyme';

import { EventDetailData } from '../../../../__mocks__/EventDetail.mock';
import EventDetailContainer from '../../../../src/components/Event/EventDetailPage/EventDetailContainer/EventDetail.container';

describe('Device Container Test Specifications', () => {

  it('DataList element count should be equal to 1', () => {
    const eventDetailData = {
      node :{
        time: EventDetailData.time,
        createdAt: EventDetailData.createdAt
      }
    };
    const cardWrapper = shallow(<EventDetailContainer
      data={eventDetailData}
    />);
    expect(cardWrapper.find('DataList').length).toEqual(1);
  });

  it('DataListItem element count should be equal to 1', () => {
    const eventDetailData = {
      node :{
        time: EventDetailData.time,
        createdAt: EventDetailData.createdAt
      }
    };
    const cardWrapper = shallow(<EventDetailContainer
      data={eventDetailData}
    />);
    expect(cardWrapper.find('DataListItem').length).toEqual(2);
  });

  it('should render successfully when device params are passed', () => {
    const div = document.createElement('div');
    const eventDetailData = {
      node :{
        time: EventDetailData.time,
        createdAt: EventDetailData.createdAt
      }
    };
    ReactDOM.render(<EventDetailContainer
      data={eventDetailData}
    />, div);
  });

});
