import React from 'react';
import ReactDOM from 'react-dom';
import { shallow } from 'enzyme';

import { EventParameterData } from '../../../../__mocks__/EventDetail.mock';
import EventParameterContainer from '../../../../src/components/Event/EventDetailPage/EventParameterContainer/EventParameter.container';

describe('Device Container Test Specifications', () => {

  it('DataList element count should be equal to 1', () => {
    const eventDetailData = {
      node :{
        data :{
          params :{
            Temperature: EventParameterData.Temperature,
            Mode: EventParameterData.Mode
          }
        }
      }
    };
    const cardWrapper = shallow(<EventParameterContainer
      data={eventDetailData}
    />);
    expect(cardWrapper.find('DataList').length).toEqual(1);
  });

  it('DataListItem element count should be equal to 2', () => {
    const eventDetailData = {
      node :{
        data :{
          params :{
            Temperature: EventParameterData.Temperature,
            Mode: EventParameterData.Mode
          }
        }
      }
    };
    const cardWrapper = shallow(<EventParameterContainer
      data={eventDetailData}
    />);
    expect(cardWrapper.find('DataListItem').length).toEqual(2);
  });

  it('should render successfully when params are passed', () => {
    const div = document.createElement('div');
    const eventDetailData = {
      node :{
        data :{
          params :{
            Temperature: EventParameterData.Temperature,
            Mode: EventParameterData.Mode
          }
        }
      }
    };
    ReactDOM.render(<EventParameterContainer
      data={eventDetailData}
    />, div);
  });

});
