import React, { PropTypes } from 'react';
import Widget from 'bluerain-ui/lib/Widget/widget';
import WidgetHeader from 'bluerain-ui/lib/Widget/widgetHeader';
import WidgetTitle from 'bluerain-ui/lib/Widget/widgetTitle';
import WidgetContent from 'bluerain-ui/lib/Widget/widgetContent';
import AirConditionerControl from './AirConditionerControl.container';

let status;
const propTypes = {
  data: PropTypes.object,
};

const defaultProps = {};

export default class AirConditionerControlWidget extends React.Component {
  render() {
    if (this.props.data && this.props.data.node && this.props.data.node.state.connected) {
      status = 'block';
    } else {
      status = 'none';
    }
    return (
      <Widget style={{ display: status }}>
        <WidgetHeader>
          <WidgetTitle>Control</WidgetTitle>
        </WidgetHeader>
        <WidgetContent>
          <AirConditionerControl {...this.props} />
        </WidgetContent>
      </Widget>
    );
  }
}

AirConditionerControlWidget.propTypes = propTypes;
AirConditionerControlWidget.defaultProps = defaultProps;
