import React from 'react';
import { toGlobalId } from 'graphql-relay';
import { storiesOf, action } from '@kadira/storybook';
import { withKnobs, text, boolean } from '@kadira/storybook-addon-knobs';
import SocketControl from './index';
import SocketControlComponent from './SocketControl.component';

const stories = storiesOf('Socket Control', module);
stories.addDecorator(withKnobs);

stories.addWithInfo('Full Control with live data', () => {

  let globalId = text('Parent Global ID', 'QXNzZXQ6dTVtMjVyR1N5Ug==');
  const parseId = text('Parent Parse ID', '');

  if (!globalId && parseId) {
    globalId = toGlobalId('Site', parseId);
  }

  return (<SocketControl assetId={globalId} />);

});


stories.addWithInfo('Socket in Off Mode', () => (
  <SocketControlComponent
    standby={boolean('Standby', true)}
    disableStandby={boolean('Disable Standby', false)}
    onStandbyToggle={action('Standby Toggle')}
  />
));

stories.addWithInfo('Socket in ON state', () => (
  <SocketControlComponent
    standby={boolean('Standby', false)}
    disableStandby={boolean('Disable Standby', false)}
    onStandbyToggle={action('Standby Toggle')}
  />
));

