import { graphql } from 'react-apollo';
import SocketControlWidget from './SocketControl.widget';
import AssetNodeQuery from './query.graphql';
import EventSaveMutation from './mutation.graphql';

const componentWithQuery = graphql(AssetNodeQuery, {
  options: ({ assetId }) => ({ variables: { assetId } })
})(SocketControlWidget);

export default graphql(EventSaveMutation)(componentWithQuery);
