import React from 'react';
import { toGlobalId } from 'graphql-relay';
import { storiesOf, action } from '@kadira/storybook';
import { withKnobs, text } from '@kadira/storybook-addon-knobs';
import SonosControl from './index';
import SonosControlComponent from './SonosControl.component';

const stories = storiesOf('Sonos Control', module);
stories.addDecorator(withKnobs);

stories.addWithInfo('Full Control with live data', () => {

  let globalId = text('Parent Global ID', 'QXNzZXQ6ZEV3bnF1RGw2ZA==');
  const parseId = text('Parent Parse ID', '');

  if (!globalId && parseId) {
    globalId = toGlobalId('Site', parseId);
  }

  return (<SonosControl assetId={globalId} />);

});


stories.addWithInfo('AC in Standby Mode', () => (
  <SonosControlComponent
    play={action('play action triggered')}
    pause={action('pause action triggered')}
    forward={action('forward action triggered')}
    backward={action('backward action triggered')}
  />
));
