import React, { PropTypes } from 'react';
import Widget from 'bluerain-ui/lib/Widget/widget';
import WidgetHeader from 'bluerain-ui/lib/Widget/widgetHeader';
import WidgetTitle from 'bluerain-ui/lib/Widget/widgetTitle';
import WidgetContent from 'bluerain-ui/lib/Widget/widgetContent';
import SonosControlContainer from './SonosControl.container';

let status;
const propTypes = {
  data: PropTypes.object,
};

const defaultProps = {};

export default class SonosControlWidget extends React.Component {
  render() {
    if (this.props.data && this.props.data.node && this.props.data.node.state.connected) {
      status = 'block';
    } else {
      status = 'none';
    }
    return (
      <Widget style={{ display: status }}>
        <WidgetHeader>
          <WidgetTitle>Sonos Controls</WidgetTitle>
        </WidgetHeader>
        <WidgetContent>
          <SonosControlContainer {...this.props} />
        </WidgetContent>
      </Widget>
    );
  }
}

SonosControlWidget.propTypes = propTypes;
SonosControlWidget.defaultProps = defaultProps;
