import React from 'react';
import { storiesOf } from '@kadira/storybook';
import LightSensorInfoContainer from './LightSensorInfo.container';
import LightSensorInfo from './index';

const stories = storiesOf('Light Sensor Info', module);

stories.add('Live Data', () => {

  const globalId = 'QXNzZXQ6dTVtMjVyR1N5Ug==';

  return (<LightSensorInfo assetId={globalId} />);
});

stories.add('With Connected Status', () => {

  const sensorData = {
    node: {
      state: {
        connected: true
      }
    }
  };
  return (
    <LightSensorInfoContainer data={sensorData} />
  );

});

stories.add('With Disconnected Status', () => {

  const sensorData = {
    node: {
      state: {
        connected: false
      }
    }
  };
  return (
    <LightSensorInfoContainer data={sensorData} />
  );

});
