import React, { PropTypes } from 'react';
import Widget from 'bluerain-ui/lib/Widget/widget';
import WidgetHeader from 'bluerain-ui/lib/Widget/widgetHeader';
import WidgetTitle from 'bluerain-ui/lib/Widget/widgetTitle';
import WidgetContent from 'bluerain-ui/lib/Widget/widgetContent';
import LightSensorInfo from './LightSensorInfo.container';

const propTypes = {
  data: PropTypes.object,
};

const defaultProps = {};
export default class LightSensorInfoWidget extends React.Component {

  render() {
    return (
      <Widget>
        <WidgetHeader>
          <WidgetTitle>Connection Status</WidgetTitle>
        </WidgetHeader>
        <WidgetContent>
          <LightSensorInfo data={this.props.data} />
        </WidgetContent>
      </Widget>
    );
  }
}
LightSensorInfoWidget.propTypes = propTypes;
LightSensorInfoWidget.defaultProps = defaultProps;
