import React from 'react';
import { storiesOf } from '@kadira/storybook';
import AssetSensorInfoContainer from './AssetSensorInfo.container';
import AssetSensorInfo from './index';

const stories = storiesOf('Asset Sensor Info', module);

stories.add('Main Component', () => {
  // const globalId = toGlobalId('Asset', id);
  const globalId = 'QXNzZXQ6NGFQeG5ZeHlmWg==';

  return (<AssetSensorInfo assetId={globalId} />);

});

stories.add('With Hardcoded Data', () => {

  const sensorData = {
    node: {
      state: {
        connected: true,
        ambientTemperature: 25,
        current: 200.456,
        power: 350.234,
        voltage: 1200.158
      }
    }
  };
  return (
    <AssetSensorInfoContainer data={sensorData} />
  );

});

