import React, { PropTypes } from 'react';
import Widget from 'bluerain-ui/lib/Widget/widget';
import WidgetHeader from 'bluerain-ui/lib/Widget/widgetHeader';
import WidgetTitle from 'bluerain-ui/lib/Widget/widgetTitle';
import WidgetContent from 'bluerain-ui/lib/Widget/widgetContent';
import AssetSensorInfo from './AssetSensorInfo.container';

const propTypes = {
  data: PropTypes.object,
};

const defaultProps = {};

export default class AssetSensorInfoWidget extends React.Component {

  render() {
    // console.log('Congtainer will be called');
    return (
      <Widget>
        <WidgetHeader>
          <WidgetTitle>Sensors</WidgetTitle>
        </WidgetHeader>
        <WidgetContent>
          <AssetSensorInfo data={this.props.data} />
        </WidgetContent>
      </Widget>
    );
  }
}

AssetSensorInfoWidget.propTypes = propTypes;
AssetSensorInfoWidget.defaultProps = defaultProps;