import React from 'react';
import { storiesOf } from '@kadira/storybook';
import TPLinkSensorInfoContainer from './TPLinkSensorInfo.container';
import TPLinkSensorInfo from './index';

const stories = storiesOf('TPLink Sensor Info', module);

stories.add('Live Data', () => {

  // const globalId = toGlobalId('TPLink', id);
  const globalId = 'QXNzZXQ6dTVtMjVyR1N5Ug==';

  return (<TPLinkSensorInfo assetId={globalId} />);

});

stories.add('With Connected Status', () => {

  const sensorData = {
    node: {
      state: {
        connected: true
      }
    }
  };
  return (
    <TPLinkSensorInfoContainer data={sensorData} />
  );

});

stories.add('With Disconnected Status', () => {

  const sensorData = {
    node: {
      state: {
        connected: false
      }
    }
  };
  return (
    <TPLinkSensorInfoContainer data={sensorData} />
  );

});
