import React, { PropTypes } from 'react';
import Widget from 'bluerain-ui/lib/Widget/widget';
import WidgetHeader from 'bluerain-ui/lib/Widget/widgetHeader';
import WidgetTitle from 'bluerain-ui/lib/Widget/widgetTitle';
import WidgetContent from 'bluerain-ui/lib/Widget/widgetContent';
import AssetsGridContainer from './AssetsGrid.container';

const propTypes = {
  title: PropTypes.string,
  assets: PropTypes.array
};

const defaultProps = {};

export default class AssetsGrid extends React.Component {

  render() {
    const { title, assets } = this.props;

    return (
      <Widget>
        <WidgetHeader>
          <WidgetTitle>{title}</WidgetTitle>
        </WidgetHeader>
        <WidgetContent>
          <AssetsGridContainer assets={assets} />
        </WidgetContent>
      </Widget>
    );
  }
}

AssetsGrid.propTypes = propTypes;
AssetsGrid.defaultProps = defaultProps;