import React from 'react';
import { storiesOf } from '@kadira/storybook';
import BillChartContainer from './BillChart.container';
import BillChartContainerIndex from './index';

const stories = storiesOf('Bill Chart', module);

stories.add('simple card', () => (<BillChartContainer label="Hello" value="Bill Chart" />));

stories.add('live data', () => (<BillChartContainerIndex assetId="QXNzZXQ6ZU4xMGlwZ2E3bw==" />));

stories.add('with proper data', () => {
  const data = {
    node: {
      events: {
        edges: [
          {
            node: {
              time: 'Jan',
              data: {
                energy: '1'
              }
            }
          },
          {
            node: {
              time: 'Feb',
              data: {
                energy: '2'
              }
            }
          },
          {
            node: {
              time: 'Mar',
              data: {
                energy: '3'
              }
            }
          }
        ]
      }
    }
  };

  return (<BillChartContainer data={data} />);
});

stories.add('with empty events', () => {
  const data = {
    node: {
      events: {}
    }
  };

  return (<BillChartContainer data={data} />);
});

stories.add('with empty edges', () => {
  const data = {
    node: {
      events: {
        edges: []
      }
    }
  };

  return (<BillChartContainer data={data} />);
});

stories.add('with single data item', () => {
  const data = {
    node: {
      events: {
        edges: [
          {
            node: {
              time: 'Jan',
              data: {
                energy: '1'
              }
            }
          }
        ]
      }
    }
  };

  return (<BillChartContainer data={data} />);
});

stories.add('with two data items', () => {
  const data = {
    node: {
      events: {
        edges: [
          {
            node: {
              time: 'Jan',
              data: {
                energy: '1'
              }
            }
          },
          {
            node: {
              time: 'Feb',
              data: {
                energy: '2'
              }
            }
          }
        ]
      }
    }
  };

  return (<BillChartContainer data={data} />);
});
