import React from 'react';
import { storiesOf } from '@kadira/storybook';
import EnergyChartContainer from './EnergyChart.container';
import EnergyChartContainerIndex from './index';

const stories = storiesOf('Energy Chart', module);

stories.add('simple card', () => (<EnergyChartContainer label="Hello" value="Energy Chart" />));

stories.add('live data', () => (<EnergyChartContainerIndex assetId="QXNzZXQ6ZU4xMGlwZ2E3bw==" />));

stories.add('with proper data', () => {
  const data = {
    node: {
      events: {
        edges: [
          {
            node: {
              time: '2016-06-03T09:10:51Z',
              data: {
                energy: '1'
              }
            }
          },
          {
            node: {
              time: '2016-06-03T09:15:51Z',
              data: {
                energy: '2'
              }
            }
          },
          {
            node: {
              time: '2016-06-03T09:20:51Z',
              data: {
                energy: '3'
              }
            }
          }
        ]
      }
    }
  };

  return (<EnergyChartContainer data={data} />);
});

stories.add('with empty events', () => {
  const data = {
    node: {
      events: {}
    }
  };

  return (<EnergyChartContainer data={data} />);
});

stories.add('with empty edges', () => {
  const data = {
    node: {
      events: {
        edges: []
      }
    }
  };

  return (<EnergyChartContainer data={data} />);
});


