import React from 'react';
import { storiesOf } from '@kadira/storybook';
import CaloriesChartContainer from './CaloriesChart.container';
import CaloriesChartContainerIndex from './index';

const stories = storiesOf('Fitbit Calories Chart', module);

stories.add('simple card', () => (<CaloriesChartContainer label="Hello" value="Calories Chart" />));

stories.add('live data', () => (<CaloriesChartContainerIndex assetId="QXNzZXQ6ZU4xMGlwZ2E3bw==" />));

stories.add('with proper data', () => {
  const data = {
    node: {
      events: {
        edges: [
          {
            node: {
              time: '2016-06-03T09:10:51Z',
              data: {
                calories: '300'
              }
            }
          },
          {
            node: {
              time: '2016-06-03T09:15:51Z',
              data: {
                calories: '550'
              }
            }
          },
          {
            node: {
              time: '2016-06-03T09:20:51Z',
              data: {
                calories: '1000'
              }
            }
          }
        ]
      }
    }
  };

  return (<CaloriesChartContainer data={data} />);
});

stories.add('with empty events', () => {
  const data = {
    node: {
      events: {}
    }
  };

  return (<CaloriesChartContainer data={data} />);
});

stories.add('with empty edges', () => {
  const data = {
    node: {
      events: {
        edges: []
      }
    }
  };

  return (<CaloriesChartContainer data={data} />);
});


stories.add('with single data item', () => {
  const data = {
    node: {
      events: {
        edges: [
          {
            node: {
              time: '2016-06-03T09:20:51Z',
              data: {
                calories: '700'
              }
            }
          }
        ]
      }
    }
  };

  return (<CaloriesChartContainer data={data} />);
});

stories.add('with two data items', () => {
  const data = {
    node: {
      events: {
        edges: [
          {
            node: {
              time: '2016-06-03T09:20:51Z',
              data: {
                calories: '400'
              }
            }
          },
          {
            node: {
              time: '2016-06-03T09:30:51Z',
              data: {
                calories: '900'
              }
            }
          }
        ]
      }
    }
  };

  return (<CaloriesChartContainer data={data} />);
});
