import React from 'react';
import { storiesOf } from '@kadira/storybook';
import DistanceChartContainer from './DistanceChart.container';
import DistanceChartContainerIndex from './index';

const stories = storiesOf('Fitbit Distance Chart', module);

stories.add('simple card', () => (<DistanceChartContainer label="Hello" value="Distance Chart" />));

stories.add('live data', () => (<DistanceChartContainerIndex assetId="QXNzZXQ6ZU4xMGlwZ2E3bw==" />));

stories.add('with proper data', () => {
  const data = {
    node: {
      events: {
        edges: [
          {
            node: {
              time: '2016-06-03T09:10:51Z',
              data: {
                distance: '3'
              }
            }
          },
          {
            node: {
              time: '2016-06-03T09:15:51Z',
              data: {
                distance: '5'
              }
            }
          },
          {
            node: {
              time: '2016-06-03T09:20:51Z',
              data: {
                distance: '10'
              }
            }
          }
        ]
      }
    }
  };

  return (<DistanceChartContainer data={data} />);
});

stories.add('with empty events', () => {
  const data = {
    node: {
      events: {}
    }
  };

  return (<DistanceChartContainer data={data} />);
});

stories.add('with empty edges', () => {
  const data = {
    node: {
      events: {
        edges: []
      }
    }
  };

  return (<DistanceChartContainer data={data} />);
});


stories.add('with single data item', () => {
  const data = {
    node: {
      events: {
        edges: [
          {
            node: {
              time: '2016-06-03T09:20:51Z',
              data: {
                distance: '700'
              }
            }
          }
        ]
      }
    }
  };

  return (<DistanceChartContainer data={data} />);
});

