import React from 'react';
import { storiesOf } from '@kadira/storybook';
import TemperatureChartContainer from './CondenserInTemperatureChart.container';
import TemperatureChartContainerIndex from './index';

const stories = storiesOf('Refrigerator Temperature Chart', module);

stories.add('simple card', () => (<TemperatureChartContainer label="Hello" value="Temperature Chart" />));

stories.add('live data', () => (<TemperatureChartContainerIndex assetId="QXNzZXQ6bXVQQXN3RGJzbQ==" />));

stories.add('with proper data', () => {
  const data = {
    node: {
      events: {
        edges: [
          {
            node: {
              time: '2016-06-03T09:10:51Z',
              data: {
                temperature: '1'
              }
            }
          },
          {
            node: {
              time: '2016-06-03T09:15:51Z',
              data: {
                temperature: '2'
              }
            }
          },
          {
            node: {
              time: '2016-06-03T09:20:51Z',
              data: {
                temperature: '3'
              }
            }
          }
        ]
      }
    }
  };

  return (<TemperatureChartContainer data={data} />);
});

stories.add('with empty events', () => {
  const data = {
    node: {
      events: {}
    }
  };

  return (<TemperatureChartContainer data={data} />);
});

stories.add('with empty edges', () => {
  const data = {
    node: {
      events: {
        edges: []
      }
    }
  };

  return (<TemperatureChartContainer data={data} />);
});


