import React from 'react';
import { storiesOf } from '@kadira/storybook';
import TimeChartContainer from './TPLinkTimeChart.container';
import TimeChartContainerIndex from './index';

const stories = storiesOf('Standby Time Chart', module);

stories.add('simple card', () => (<TimeChartContainer label="Hello" value="Standby Time Chart" />));

stories.add('live data', () => (<TimeChartContainerIndex assetId="QXNzZXQ6ZU4xMGlwZ2E3bw==" />));

stories.add('with proper data', () => {
  const data = {
    node: {
      events: {
        edges: [
          {
            node: {
              time: '2016-06-03T09:10:51Z',
              data: {
                standby: true
              }
            }
          },
          {
            node: {
              time: '2016-06-03T09:15:51Z',
              data: {
                standby: true
              }
            }
          },
          {
            node: {
              time: '2016-06-03T09:20:51Z',
              data: {
                standby: true
              }
            }
          }
        ]
      }
    }
  };

  return (<TimeChartContainer data={data} />);
});

stories.add('with empty events', () => {
  const data = {
    node: {
      events: {}
    }
  };

  return (<TimeChartContainer data={data} />);
});

stories.add('with empty edges', () => {
  const data = {
    node: {
      events: {
        edges: []
      }
    }
  };

  return (<TimeChartContainer data={data} />);
});

