import React, { PropTypes } from 'react';
import VoltageChartComponent from './VoltageChart.component';

const propTypes = {
  data: PropTypes.object,
  tooltip: PropTypes.bool,
  startDate: PropTypes.string,
  endDate: PropTypes.string,
  label: PropTypes.string,
  showGraph: PropTypes.bool,
  linkTag: PropTypes.oneOfType([
    React.PropTypes.func,
    React.PropTypes.string
  ]),
  to: PropTypes.string,
  color: PropTypes.string
};

const defaultPropTypes = {
  startDate: '01/01/1970',
  endDate: '01/01/2099',
  tooltip: true,
  showGraph: true,
  label: 'Average Voltage',
  linkTag: 'a',
  to: '#'
};

const voltageThreshold = 100;
export default class VoltageChartContainer extends React.Component {

  render() {
    const {
      color,
      tooltip,
      data: dbData,
      label,
      linkTag,
      to,
      showGraph,
      ...rest
    } = this.props;

    let data = [];
    let lastReading = 'No data found';

    if (this.props.data === undefined || this.props.data.loading) {
      lastReading = 'Loading data';
    }

    try {

      // let dataFromQuery = dbData.node.events.edges;
      let dataFromQuery = dbData.Event.EventFind.edges;
      dataFromQuery = dataFromQuery.map(item => item.node);

      dataFromQuery.forEach((item) => {
        if (item.data && item.data.voltage !== undefined) {
          data.push({
            time: new Date(item.time),
            voltage: item.data.voltage
          });
        }
      });

      data = data.filter(el =>
        el.voltage >= voltageThreshold
      );

      if (data.length > 0) {
        data.sort((a, b) => ( a.time > b.time ) ? 1 : ((b.time > a.time) ? -1 : 0));

        const sum = data.reduce((a, b) => a + b.voltage, 0);
        const average = sum / data.length;

        lastReading = `${average.toFixed(0)} V`;
      }

    } catch (error) {
      // console.info('Voltage Chart no data found');
    }

    if (showGraph) {
      return (
        <VoltageChartComponent
          data={data}
          x="time"
          y={datum => (datum.voltage)}
          color={this.props.color}
          label={label}
          value={lastReading}
          tooltip={this.props.tooltip}
          linkTag={linkTag}
          to={to}
          {...rest}
        />

      );
    }
    return (
      <VoltageChartComponent
        color={color}
        label={label}
        value={lastReading}
        tooltip={tooltip}
        linkTag={linkTag}
        to={to}
        {...rest}
      />
    );
  }
}

VoltageChartContainer.propTypes = propTypes;
VoltageChartContainer.defaultProps = defaultPropTypes;
