import React from 'react';
import { storiesOf } from '@kadira/storybook';
import VoltageChartContainer from './VoltageChart.container';
import VoltageChartContainerIndex from './index';

const stories = storiesOf('Voltage Chart', module);

stories.add('simple card', () => (<VoltageChartContainer label="Hello" value="Voltage Chart" />));

stories.add('live data', () => (<VoltageChartContainerIndex assetId="QXNzZXQ6ZU4xMGlwZ2E3bw==" />));

stories.add('with proper data', () => {
  const data = {
    node: {
      events: {
        edges: [{
          node: {
            time: '2016-06-03T09:10:51Z',
            data: {
              voltage: '5'
            }
          }
        },
        {
          node: {
            time: '2016-06-03T09:15:51Z',
            data: {
              voltage: '10'
            }
          }
        },
        {
          node: {
            time: '2016-06-03T09:20:51Z',
            data: {
              voltage: '15'
            }
          }
        }
        ]
      }
    }
  };

  return (<VoltageChartContainer data={data} />);
});

stories.add('with empty events', () => {
  const data = {
    node: {
      events: {}
    }
  };

  return (<VoltageChartContainer data={data} />);
});

stories.add('with empty edges', () => {
  const data = {
    node: {
      events: {
        edges: []
      }
    }
  };

  return (<VoltageChartContainer data={data} />);
});

stories.add('with single data item', () => {
  const data = {
    node: {
      events: {
        edges: [
          {
            node: {
              time: '2016-06-03T09:20:51Z',
              data: {
                voltage: '15'
              }
            }
          }
        ]
      }
    }
  };

  return (<VoltageChartContainer data={data} />);
});

stories.add('with two data items', () => {
  const data = {
    node: {
      events: {
        edges: [
          {
            node: {
              time: '2016-06-03T09:20:51Z',
              data: {
                voltage: '5'
              }
            }
          },
          {
            node: {
              time: '2016-06-03T09:30:51Z',
              data: {
                voltage: '15'
              }
            }
          }
        ]
      }
    }
  };

  return (<VoltageChartContainer data={data} />);
});
