import React from 'react';
import { storiesOf } from '@kadira/storybook';
import FitbitSensorInfoContainer from './FitbitSensorInfo.container';
import FitbitSensorInfo from './index';

const stories = storiesOf('Fitbit Sensor Info', module);

stories.add('Live Data', () => {

  // const globalId = toGlobalId('Fitbit', id);
  const globalId = 'QXNzZXQ6dTVtMjVyR1N5Ug==';

  return (<FitbitSensorInfo assetId={globalId} />);

});

stories.add('With Connected Status', () => {

  const sensorData = {
    node: {
      state: {
        connected: true,
        duration: 1200,
        distance: 3,
        calories: 1580
      }
    }
  };
  return (
    <FitbitSensorInfoContainer data={sensorData} />
  );

});

stories.add('With Disconnected Status', () => {

  const sensorData = {
    node: {
      state: {
        connected: false,
        duration: 1200,
        distance: 3,
        calories: 1580
      }
    }
  };
  return (
    <FitbitSensorInfoContainer data={sensorData} />
  );

});
