import React, { PropTypes } from 'react';
import 'bluerain-bootstrap-theme/dist/css/components/data-list.css';
import { Gmaps, Marker } from 'react-gmaps';

const propTypes = {
  data: PropTypes.object,
};

const defaultProps = {};

export default class RefrigeratorLocationMapView extends React.Component {

  render() {
    if (!this.props.data.node || !this.props.data.node.state) {
      return (<div />);
    }

    let {
      latitude,
      longitude
    } = this.props.data.node.state.location;
    if (!latitude || !longitude) {
      latitude = 31387640;
      longitude = 74148855;
    }
    const lat = latitude / 1000000;
    const lng = longitude / 1000000;
    return (
      <Gmaps
        width={'100%'}
        height={'30vh'}
        lat={lat}
        lng={lng}
        zoom={15}
        streetViewControl={false}
        params={{ v: '3.exp', key: 'AIzaSyCgVYH02v3XYmCSIokoLOZzvUqJG6ZamMY' }}
      >
        <Marker
          lat={lat}
          lng={lng}
        />
      </Gmaps>
    );
  }
}

RefrigeratorLocationMapView.propTypes = propTypes;
RefrigeratorLocationMapView.defaultProps = defaultProps;
