import React from 'react';
import { storiesOf } from '@kadira/storybook';
import SonosSensorInfoContainer from './SonosSensorInfo.container';
import SonosSensorInfo from './index';

const stories = storiesOf('Sonos Sensor Info', module);

stories.add('Live Data', () => {

  // const globalId = toGlobalId('Sonos', id);
  const globalId = 'QXNzZXQ6dTVtMjVyR1N5Ug==';

  return (<SonosSensorInfo assetId={globalId} />);

});

stories.add('With Connected Status', () => {

  const sensorData = {
    node: {
      state: {
        connected: true,
        song: 'Gone too soon',
        album: 'Dangerous',
        artist: 'Micheal Jackson',
        image: 'https://s3-us-west-2.amazonaws.com/bluerainimages/dangerous.jpg'
      }
    }
  };
  return (
    <SonosSensorInfoContainer data={sensorData} />
  );

});

