import React, { PropTypes } from 'react';
import Widget from 'bluerain-ui/lib/Widget/widget';
import WidgetHeader from 'bluerain-ui/lib/Widget/widgetHeader';
import WidgetTitle from 'bluerain-ui/lib/Widget/widgetTitle';
import WidgetContent from 'bluerain-ui/lib/Widget/widgetContent';
import SonosSensorInfo from './SonosSensorInfo.container';

const propTypes = {
  data: PropTypes.object,
};

const defaultProps = {};

export default class SonosSensorInfoWidget extends React.Component {

  render() {
    return (
      <Widget>
        <WidgetHeader>
          <WidgetTitle>Sensors</WidgetTitle>
        </WidgetHeader>
        <WidgetContent>
          <SonosSensorInfo data={this.props.data} />
        </WidgetContent>
      </Widget>
    );
  }
}

SonosSensorInfoWidget.propTypes = propTypes;
SonosSensorInfoWidget.defaultProps = defaultProps;
