import React, { PropTypes } from 'react';
import Widget from 'bluerain-ui/lib/Widget/widget';
import WidgetHeader from 'bluerain-ui/lib/Widget/widgetHeader';
import WidgetTitle from 'bluerain-ui/lib/Widget/widgetTitle';
import WidgetContent from 'bluerain-ui/lib/Widget/widgetContent';
import AddDeviceFirstScreenComponent from './AddDeviceFirstScreen.component';

const propTypes = {
  onSubmit: PropTypes.func
};

const defaultProps = {
  onSubmit: ''
};


class SiteFormWidget extends React.Component {
  render() {
    let submitAction = '';
    if (typeof this.props.onSubmit === 'function') {
      submitAction = this.props.onSubmit;
    }

    return (
      <Widget>
        <WidgetHeader>
          <WidgetTitle>Connect to your AC</WidgetTitle>
        </WidgetHeader>
        <WidgetContent>
          <AddDeviceFirstScreenComponent onSubmit={submitAction} />
        </WidgetContent>
      </Widget>
    );
  }
}

SiteFormWidget.propTypes = propTypes;
SiteFormWidget.defaultProps = defaultProps;

export default SiteFormWidget;
