import React, { PropTypes } from 'react';
import Widget from 'bluerain-ui/lib/Widget/widget';
import WidgetHeader from 'bluerain-ui/lib/Widget/widgetHeader';
import WidgetTitle from 'bluerain-ui/lib/Widget/widgetTitle';
import WidgetContent from 'bluerain-ui/lib/Widget/widgetContent';
import AddDeviceSecondScreenComponent from './AddDeviceSecondScreen.component';

const propTypes = {
  networks: PropTypes.array,
  clickHandler: PropTypes.func
};

class SiteFormWidget extends React.Component {

  constructor() {
    super();
    this.networkList = [];
    this.clickHandler = (name) => {
      // console.log(`${name} clicked`);
    };
  }

  render() {
    this.networkList = this.props.networks || this.networkList;
    this.clickHandler = this.props.clickHandler || this.clickHandler;

    this.networkList = this.props.networks;

    let i = 0;
    return (
      <Widget>
        <WidgetHeader>
          <WidgetTitle>Now connect this AC with one of the following internet connection</WidgetTitle>
        </WidgetHeader>
        <WidgetContent>
          {
            this.networkList.map(item => (
              <AddDeviceSecondScreenComponent
                key={i++} wifiName={item.ssid} secure={item.secure}
                onClick={this.clickHandler}
              />))
          }
        </WidgetContent>
      </Widget>
    );
  }
}

SiteFormWidget.propTypes = propTypes;

export default SiteFormWidget;
