import React, { PropTypes } from 'react';
import Widget from 'bluerain-ui/lib/Widget/widget';
import WidgetHeader from 'bluerain-ui/lib/Widget/widgetHeader';
import WidgetTitle from 'bluerain-ui/lib/Widget/widgetTitle';
import WidgetContent from 'bluerain-ui/lib/Widget/widgetContent';
import { withRouter } from 'react-router';
import UserInfoTransfer from './UserInfoTransfer.component';


const propTypes = {
  params: PropTypes.object,
  location: PropTypes.object
};

const defaultProps = {};

class UserInfo extends React.Component {

  render() {
    if (!this.props.params || !this.props.params.userId || !this.props.location || !this.props.location.query || !this.props.location.query.current_user) {
      return (<div />);
    }
    const currentUser = this.props.location.query.current_user;
    return (
      <Widget>
        <WidgetHeader>
          <WidgetTitle>User Information</WidgetTitle>
        </WidgetHeader>
        <WidgetContent>
          <UserInfoTransfer
            assetId={this.props.params.assetId} ownerId={currentUser}
            userId={this.props.params.userId}
          />
        </WidgetContent>
      </Widget>
    );
  }
}

export default withRouter(UserInfo);

UserInfo.propTypes = propTypes;
UserInfo.defaultProps = defaultProps;
