import React, { PropTypes } from 'react';
import Widget from 'bluerain-ui/lib/Widget/widget';
import WidgetHeader from 'bluerain-ui/lib/Widget/widgetHeader';
import WidgetTitle from 'bluerain-ui/lib/Widget/widgetTitle';
import WidgetContent from 'bluerain-ui/lib/Widget/widgetContent';
import DeviceInfo from './DeviceNetworkSettings.stateful';

const propTypes = {
  data: PropTypes.object,
};

const defaultProps = {};
export default class DeviceInfoWidget extends React.Component {

  render() {
    return (
      <Widget>
        <WidgetHeader>
          <WidgetTitle>Network Settings</WidgetTitle>
        </WidgetHeader>
        <WidgetContent>
          <DeviceInfo data={this.props.data} />
        </WidgetContent>
      </Widget>
    );
  }
}

DeviceInfoWidget.propTypes = propTypes;
DeviceInfoWidget.defaultProps = defaultProps;
