import React, { PropTypes } from 'react';
import Widget from 'bluerain-ui/lib/Widget/widget';
import WidgetHeader from 'bluerain-ui/lib/Widget/widgetHeader';
import WidgetTitle from 'bluerain-ui/lib/Widget/widgetTitle';
import WidgetContent from 'bluerain-ui/lib/Widget/widgetContent';
import SideBar from './SideBar.component';

const propTypes = {
  assetId: PropTypes.string,
  routes: PropTypes.array
};

const defaultProps = {};


export default class SideBarWidget extends React.Component {


  render() {
    return (
      <Widget>
        <WidgetHeader>
          <WidgetTitle />
        </WidgetHeader>
        <WidgetContent>
          <SideBar assetId={this.props.assetId} routes={this.props.routes} />
        </WidgetContent>
      </Widget>
    );
  }
}

SideBarWidget.propTypes = propTypes;
SideBarWidget.defaultProps = defaultProps;
