import { graphql } from 'react-apollo';
import Query from './query.graphql';

export default function gql(component) {
  return graphql(Query, {
    options: ({ assetId }) => (
      { variables: { assetId } },
        { pollInterval: 3000 }
    )
  })(component);
}
