import React, { PropTypes } from 'react';
import moment from 'moment';
import DataList from 'bluerain-ui/lib/DataList/DataList';
import DataListItem from 'bluerain-ui/lib/DataList/DataListItem';
import DataListItemLabel from 'bluerain-ui/lib/DataList/DataListItemLabel';
import DataListItemValue from 'bluerain-ui/lib/DataList/DataListItemValue';


const propTypes = {
  data: PropTypes.object,
};

const defaultProps = {};

export default class EventDetail extends React.Component {

  render() {
    try {
      const {
        time,
        createdAt,
      } = this.props.data.node;
      if (this.props.data.node) {
        return (
          <DataList>
            <DataListItem action={false} chevronRight={false}>
              <DataListItemLabel>
                Time
              </DataListItemLabel>
              <DataListItemValue>
                {moment(time).format('LLL')}
              </DataListItemValue>
            </DataListItem>
            <DataListItem action={false} chevronRight={false}>
              <DataListItemLabel>
                Added to Database
              </DataListItemLabel>
              <DataListItemValue>
                {moment(createdAt).format('LLL')}
              </DataListItemValue>
            </DataListItem>
          </DataList>
        );
      }
      return (<div />);
    } catch (error) {
      console.log(error);
    }

  }
}

EventDetail.propTypes = propTypes;
EventDetail.defaultProps = defaultProps;
