import { stateful } from 'bluerain-ui/lib/StatefulComponent';
// import TimelineContainer from './Timeline.container';
import Page from './Timeline.live';

const checkData = function(props, dataProp) {
  try {
    const data = props.data.node.posts.edges;
    if (data && data.length > 0) {
      return true;
    }
  } catch (error) {
    return false;
  }
  return false;
};

const params = {
  dataField: 'node',
  checkData
};

export default stateful(Page, params);
