import React, { PropTypes } from 'react';
import Widget from 'bluerain-ui/lib/Widget/widget';
import WidgetHeader from 'bluerain-ui/lib/Widget/widgetHeader';
import WidgetTitle from 'bluerain-ui/lib/Widget/widgetTitle';
import WidgetContent from 'bluerain-ui/lib/Widget/widgetContent';
import TimelineStateful from './Timeline.stateful';
import graphql from './Timeline.graphql';

const TimelineWithData = graphql(TimelineStateful);

const propTypes = {
  assetId: PropTypes.String
};

export default class Timeline extends React.Component {

  render() {
    console.log('assetId', this.props.assetId);
    return (
      <Widget>
        <WidgetHeader>
          <WidgetTitle>Timeline</WidgetTitle>
        </WidgetHeader>
        <WidgetContent>
          <TimelineWithData assetId={this.props.assetId} />
        </WidgetContent>
      </Widget>
    );
  }
}

Timeline.propTypes = propTypes;
