import React, { PropTypes } from 'react';
import EnergyReportComponent from './EnergyReport.component';

const propTypes = {
  data: PropTypes.object,
  tooltip: PropTypes.bool,
  startDate: PropTypes.string,
  endDate: PropTypes.string,
  color: PropTypes.string
};

const defaultPropTypes = {
  startDate: '01/01/1970',
  endDate: '01/01/2099',
  tooltip: true,
  color: 'yellow'
};

export default class EnergyReportContainer extends React.Component {

  render() {

    const {
      color,
      tooltip,
      data: dbData,
      ...rest
    } = this.props;

    const data = [];

    try {
      let dataFromQuery = dbData.Event.EventFind.edges;
      dataFromQuery = dataFromQuery.map(item => item.node);

      dataFromQuery.forEach((item) => {
        if (item.data && item.data.energy !== undefined) {
          data.push({
            time: new Date(item.time),
            energy: item.data.energy
          });
        }
      });

      if (data.length > 0) {
        data.sort((a, b) => (a.time > b.time) ? 1 : ((b.time > a.time) ? -1 : 0));
      }

    } catch (error) {
      console.info('Energy Report: no data found');
    }

    return (
      <EnergyReportComponent
        data={data}
        x="time"
        y={datum => (datum.energy)}
        tooltip={tooltip}
        color={color}
        {...rest}
      />
    );
  }
}

EnergyReportContainer.propTypes = propTypes;
EnergyReportContainer.defaultProps = defaultPropTypes;
