import WidgetContent from 'bluerain-ui/lib/Widget/widgetContent';
import SiteEditFormComponent from './SiteEditForm.component';
import SiteDeleteComponent from './SiteDelete.component';

const propTypes = {
  siteId: PropTypes.string
};

export default class SiteEditFormWidget extends React.Component {

  render() {
    return (
      <div>
      <Widget>
        <WidgetHeader>
          <WidgetTitle>Edit Site</WidgetTitle>
        </WidgetHeader>
        <WidgetContent>
          <SiteEditFormComponent siteId={this.props.siteId} />
        </WidgetContent>
      </Widget>
        <Widget>
          <WidgetHeader>
            <WidgetTitle style={{ color: '#d9534f' }}>Danger</WidgetTitle>
          </WidgetHeader>
          <WidgetContent>
            <SiteDeleteComponent siteId={this.props.siteId} />
          </WidgetContent>
        </Widget>
      </div>
    );
  }
}

SiteEditFormWidget.propTypes = propTypes;
