import React from 'react';
import { storiesOf } from '@kadira/storybook';
import { text, object } from '@kadira/storybook-addon-knobs';
import { toGlobalId } from 'graphql-relay';
import SitesGridComponent from './SitesGrid.container';

const stories = storiesOf('Sites Grid', module);

stories.add('With only name', () => {

  let globalId = text('Parent Global ID', '');
  const parseId = text('Parent Parse ID', '');

  if (!globalId && parseId) {
    globalId = toGlobalId('Site', parseId);
  }

  const title = text('Enter Title', 'Card Title');
  const myData = object('Data', {
    viewer: {
      sites: {
        edges: [{
          node: {
            id: '123',
            name: title
          }
        }]
      }
    }
  });

  return (<SitesGridComponent data={myData} />);

});


stories.add('With Site Name and Image', () => {

  let globalId = text('Parent Global ID', '');
  const parseId = text('Parent Parse ID', '');

  if (!globalId && parseId) {
    globalId = toGlobalId('Site', parseId);
  }

  const myData = {
    viewer: {
      sites: {
        edges: [{
          node: {
            id: '123',
            name: 'Orient Office',
            image: {
              url: 'https://www.google.com.pk/images/branding/googlelogo/1x/googlelogo_color_272x92dp.png'
            }
          }
        }]
      }
    }
  };


  return (<SitesGridComponent data={myData} />);

});

