import React, { PropTypes } from 'react';
import Widget from 'bluerain-ui/lib/Widget/widget';
import WidgetHeader from 'bluerain-ui/lib/Widget/widgetHeader';
import WidgetTitle from 'bluerain-ui/lib/Widget/widgetTitle';
import WidgetContent from 'bluerain-ui/lib/Widget/widgetContent';
import SitesGridStateful from './SitesGrid.stateful';
import graphql from './SitesGrid.graphql';
const SitesGridWithData = graphql(SitesGridStateful);

const propTypes = {
  assetId: PropTypes.String
};


export default class SitesGrid extends React.Component {

  render() {
    return (
      <Widget>
        <WidgetHeader>
          <WidgetTitle>My Sites</WidgetTitle>
        </WidgetHeader>
        <WidgetContent>
          <SitesGridWithData assetId={this.props.assetId} />
        </WidgetContent>
      </Widget>
    );
  }
}

SitesGrid.propTypes = propTypes;
