import React, { PropTypes } from 'react';
import StatefulPage from './AssetPage.stateful';
import PageGQL from './AssetPage.graphql';

const propTypes = {
  params: PropTypes.object
};

const defaultProps = {
  params: {}
};

class Page extends React.Component {

  render() {
    const assetId = this.props.params.assetId;
    const Component = PageGQL(StatefulPage, assetId);
    const props = this.props;

    return (
      <Component assetId={assetId} {...props} />
    );
  }
}

Page.propTypes = propTypes;
Page.defaultProps = defaultProps;

export default Page;
