import React, { PropTypes } from 'react';
import { Container, Row, Col } from 'reactstrap';
import DeviceInfo from '../../components/Device/DeviceSettings/DeviceInfo';
import SideBar from '../../components/Device/DeviceSettings/SideBar.widget';

const propTypes = {
  routes: PropTypes.array,
  params: PropTypes.object
};

const defaultProps = {
  title: 'Device Info'
};

const DeviceInfoPage = (props) => {
  const { assetId } = props.params;
  return (
    <div>
      <section>
        <Container>
          <Row>
            <Col lg={{ size: 3 }}>
              <SideBar assetId={assetId} routes={props.routes} />
            </Col>
            <Col lg={{ size: 9 }} className="col-faded">
              <DeviceInfo assetId={assetId} />
            </Col>
          </Row>
        </Container>
      </section>
    </div>
  );
};

DeviceInfoPage.propTypes = propTypes;
DeviceInfoPage.defaultProps = defaultProps;

export default DeviceInfoPage;
