import React, { PropTypes } from 'react';
import { Container, Row, Col } from 'reactstrap';
import DeviceNetworkSettings from '../../components/Device/DeviceSettings/DeviceNetworkSettings';
import SideBar from '../../components/Device/DeviceSettings/SideBar.widget';

const propTypes = {
  routes: PropTypes.array,
  params: PropTypes.func
};

const defaultProps = {
  title: 'Device Network Settings'
};

const DeviceNetworkSettingsPage = (props) => {
  const { assetId } = props.params;
  return (
    <div>
      <section>
        <Container>
          <Row>
            <Col lg={{ size: 3 }}>
              <SideBar assetId={assetId} routes={props.routes} />
            </Col>
            <Col lg={{ size: 9 }} className="col-faded">
              <DeviceNetworkSettings assetId={assetId} />
            </Col>
          </Row>
        </Container>
      </section>
    </div>
  );
};

DeviceNetworkSettingsPage.propTypes = propTypes;
DeviceNetworkSettingsPage.defaultProps = defaultProps;

export default DeviceNetworkSettingsPage;
