import React, { PropTypes } from 'react';
import { Container, Row, Col } from 'reactstrap';
import EventDetail from '../../components/Event/EventDetailPage';

const propTypes = {
  params: PropTypes.object
};

const defaultProps = {
  title: 'Event Detail'
};

const EventDetailPage = (props) => {
  const { eventId } = props.params;
  return (
    <div>
      <section>
        <Container>
          <Row>
            <Col md={{ size: 8, offset: 2 }} className="col-faded">
              <EventDetail eventId={eventId} />
            </Col>
          </Row>
        </Container>
      </section>
    </div>
  );
};

EventDetailPage.propTypes = propTypes;
EventDetailPage.defaultProps = defaultProps;

export default EventDetailPage;
