import React from 'react';
import { storiesOf } from '@kadira/storybook';
import IconComponent from './Icon.component';


const stories = storiesOf('Icon', module);


stories.addWithInfo('Default Icon', () => (
  <IconComponent  />
));

stories.addWithInfo('Gradient Background', () => (
  <IconComponent
    gradient
    backgroundColors={['red', 'orange']}
  />
));

stories.addWithInfo('Shadow', () => (
  <IconComponent
    gradient
    shadow="20px"
    backgroundColors={['red', 'orange']}
  />
));

stories.addWithInfo('With icon text ', () => (
  <IconComponent
    gradient
    shadow="20px"
    text="App Name"
    backgroundColors={['red', 'orange']}
  />
));

stories.addWithInfo('Icon text color changed', () => (
  <IconComponent
    gradient
    shadow="20px"
    textColor="red"
    text="App Name"
    backgroundColors={['red', 'orange']}
  />
));

stories.addWithInfo('Increased Size', () => (
  <IconComponent
    gradient
    size="160px"
    backgroundColors={['red', 'orange']}
  />
));

stories.addWithInfo('Image Background', () => (
  <IconComponent
    src="http://lorempixel.com/400/200/"
  />
));

stories.addWithInfo('Icon Changed', () => (
  <IconComponent
    name="star"
    gradient
    backgroundColors={['pink', 'red']}
  />
));
stories.addWithInfo('png icon', () => (
  <IconComponent
    shadow="20px"
    text="App Name"
    png="https://thumb.ibb.co/e0yaWF/app_icon.png"
  />
));
